mapper.select:
SELECT DISTINCT r.* FROM resume r
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_name INNER JOIN resume_field_definition rfd_name ON rfv_name.field_id = rfd_name.field_id WHERE rfv_name.resume_id = r.resume_id AND rfd_name.field_key = 'name' AND rfv_name.field_value LIKE CONCAT('%', #{name}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_major INNER JOIN resume_field_definition rfd_major ON rfv_major.field_id = rfd_major.field_id WHERE rfv_major.resume_id = r.resume_id AND rfd_major.field_key = 'major' AND rfv_major.field_value LIKE CONCAT('%', #{major}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_ed INNER JOIN resume_field_definition rfd_ed ON rfv_ed.field_id = rfd_ed.field_id WHERE rfv_ed.resume_id = r.resume_id AND rfd_ed.field_key = 'expected_departments' AND rfv_ed.field_value LIKE CONCAT('%', #{expectedDepartment}, '%') )
AND r.cycle_id = #{cycleId}
AND r.status IN
#{statusValue}
AND r.status = #{status}
ORDER BY r.created_at DESC

SELECT DISTINCT r.* FROM resume r
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_name INNER JOIN resume_field_definition rfd_name ON rfv_name.field_id = rfd_name.field_id WHERE rfv_name.resume_id = r.resume_id AND rfd_name.field_key = 'name' AND rfv_name.field_value LIKE CONCAT('%', #{name}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_major INNER JOIN resume_field_definition rfd_major ON rfv_major.field_id = rfd_major.field_id WHERE rfv_major.resume_id = r.resume_id AND rfd_major.field_key = 'major' AND rfv_major.field_value LIKE CONCAT('%', #{major}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_ed INNER JOIN resume_field_definition rfd_ed ON rfv_ed.field_id = rfd_ed.field_id WHERE rfv_ed.resume_id = r.resume_id AND rfd_ed.field_key = 'expected_departments' AND rfv_ed.field_value LIKE CONCAT('%', #{expectedDepartment}, '%') )
AND r.cycle_id = #{cycleId}
AND r.status IN
#{statusValue}
AND r.status = #{status}
ORDER BY r.created_at DESC LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT r.resume_id) FROM resume r
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_name INNER JOIN resume_field_definition rfd_name ON rfv_name.field_id = rfd_name.field_id WHERE rfv_name.resume_id = r.resume_id AND rfd_name.field_key = 'name' AND rfv_name.field_value LIKE CONCAT('%', #{name}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_major INNER JOIN resume_field_definition rfd_major ON rfv_major.field_id = rfd_major.field_id WHERE rfv_major.resume_id = r.resume_id AND rfd_major.field_key = 'major' AND rfv_major.field_value LIKE CONCAT('%', #{major}, '%') )
AND EXISTS ( SELECT 1 FROM resume_field_value rfv_ed INNER JOIN resume_field_definition rfd_ed ON rfv_ed.field_id = rfd_ed.field_id WHERE rfv_ed.resume_id = r.resume_id AND rfd_ed.field_key = 'expected_departments' AND rfv_ed.field_value LIKE CONCAT('%', #{expectedDepartment}, '%') )
AND r.cycle_id = #{cycleId}
AND r.status IN
#{statusValue}
AND r.status = #{status}

mapper.update:
UPDATE resume 
SET status = #{status}, submitted_at = #{submittedAt}, updated_at = NOW()
WHERE resume_id = #{resumeId}
