mapper.update:
UPDATE user
username = #{username}, password = #{password}, email = #{email}, role = #{role}, name = #{name}, phone = #{phone}, dept = #{dept}, status = #{status}, is_member = #{isMember},
WHERE user_id = #{userId}

UPDATE user
SET password = #{password}
WHERE user_id = #{userId}

mapper.select:
